        $.widget("custom.tablecomplete", $.ui.autocomplete, {
            _create: function() {
                this._super();
                this.widget().menu("option", "items", "> li:not(.ui-autocomplete-header)");
            },
            _renderMenu(ul, items) {
                var self = this;
                var count = 1;
                $.each(items, function(index, item) {
                    if (item.in_use.trim().toUpperCase() == "YES") {
                        count++;
                        if (count < 10) {
                            self._renderItemData(ul, item);
                        }
                    }
                });
            },
            _renderItemData(ul, item) {
                return this._renderItem(ul, item).data("ui-autocomplete-item", item);
            },
            _renderItem(ul, item) {
                var $li = $("<li class='ui-menu-item' role='presentation'></li>");
                //var $content = "<div class='row ui-menu-item-wrapper'>" + "<div class='col-xs-12'>" + item.postcode + "</div>" + "</div>";
                $li.html(item.postcode); //$content
        
                return $li.appendTo(ul);
            }
        });
        
        var xhr;
        // create the autocomplete
        var autocomplete = $("#real_estate_search_london_postcodes input").tablecomplete({
            delay: 0,
            source: function( request, response ) {
                var regex = new RegExp(request.term, 'i');
                var bigLetter = (request.term.substring(0, 1).toUpperCase());
                if(xhr){
                    xhr.abort();
                }
                xhr = $.ajax({
                    //url: "http://ivanstojanov.com/postcodes/postcodes" + bigLetter + ".json",
                    url: "http://localhost:8080/wordpress/postcodesdb/postcodes" + bigLetter + ".json",
                    dataType: "json",
                    cache: false,
                    success: function(data) {
                        response($.map(data, function(item) {
                            if(regex.test(item.postcode)){
                                return {
                                    postcode: item.postcode,
                                    in_use: item.in_use,
                                    latitude: item.latitude,
                                    longitude: item.longitude,
                                    district: item.district,
                                    ward: item.ward,
                                    constituency: item.constituency
                                };
                            }
                        }));
                    }
                });
            },
            minlength:0,
            select: function( event , ui ) {
                $("#real_estate_search_london_postcodes input").val(ui.item.postcode);
                $("#real_estate_property_location_zip input").val(ui.item.postcode);
                $("#real_estate_property_location_latitude input").val(ui.item.latitude);
                $("#real_estate_property_location_longitude input").val(ui.item.longitude);
            }
        });
        
